import { Injectable } from '@angular/core';
import { Actions, createEffect, ofType } from '@ngrx/effects';
import { of } from 'rxjs';
import { catchError, map, mergeMap } from 'rxjs/operators';
import { FudbalskitimService } from '../services/fudbalskitim.service';
import * as TimActions from 'src/app/store/igrac.action';
import * as IgracActions from 'src/app/store/igrac.action';

@Injectable()
export class TimEffects {
  constructor(
    private actions$: Actions,
    private fudbalskiTimService: FudbalskitimService
  ) {}

  loadTimovi$ = createEffect(() =>
    this.actions$.pipe(
      ofType(TimActions.loadTimovi),
      mergeMap(() =>
        this.fudbalskiTimService.getSviTimovi()
          .pipe(
            map(timovi => TimActions.loadTimoviSuccess({ timovi })),
            catchError(() => of({ type: 'load error' }))
          )
      )
    )
  );

  updateTim$ = createEffect(() =>
    this.actions$.pipe(
      ofType(TimActions.updateTim),
      mergeMap(action =>
        this.fudbalskiTimService.updateTimIgraci(
          action.timId, action.igraciTeren, action.igraciZamena, action.tim
        )
          .pipe(
            map(() => TimActions.updateTimSuccess()),
            catchError(() => of({ type: 'load error' }))
          )
      )
    )
  );

  swapPlayers$ = createEffect(() =>
  this.actions$.pipe(
    ofType(TimActions.swapPlayers),
    switchMap(action =>
      forkJoin([
        this.fudbalskiTimService.getTimById(action.timId),
        this.fudbalskiTimService.getIgracById(action.igracIzlaziId),
        this.fudbalskiTimService.getIgracById(action.igracUlaziId)
      ]).pipe(
        switchMap(([tim, igracIzlazi, igracUlazi]) =>
          this.fudbalskiTimService.getIgraciByPozicija(igracIzlazi.pozicija, tim.id).pipe(
            map(igraciNaPoziciji => ({ tim, igracIzlazi, igracUlazi, igraciNaPoziciji })),
            catchError(() => of({ type: 'load error' }))
          )
        ),
        map(({ tim, igracIzlazi, igracUlazi, igraciNaPoziciji }) => {
          const updatedTim = { ...tim };

          // Implementiraj logiku zamene igrača koristeći informacije dobijene tokom ovog niza koraka

          return TimActions.updateTim({
            timId: updatedTim.id,
            igraciTeren: updatedTim.igraciTeren,
            igraciZamena: updatedTim.igraciZamena,
            tim: updatedTim
          });
        }),
        catchError(() => of({ type: 'swap error' }))
      )
    )
  )
);


  
}

@Injectable()
export class IgracEffects {
  constructor(
    private actions$: Actions,
    private fudbalskiTimService: FudbalskitimService
  ) {}

  loadIgraci$ = createEffect(() =>
    this.actions$.pipe(
      ofType(IgracActions.loadIgraci),
      mergeMap(() =>
        this.fudbalskiTimService.getSviIgraci()
          .pipe(
            map(igraci => IgracActions.loadIgraciSuccess({ igraci })),
            catchError(() => of({ type: 'load error' }))
          )
      )
    )
  );
}
