import { createReducer, on } from "@ngrx/store";
import { Igrac } from "../models/igrac";
import { Tim } from "../models/tim";
import { buyIgrac, loadGolmaniSuccess, loadIgraciSuccess, loadIgraciTerenSuccess, loadIgraciZamenaSuccess, loadNapadaciSuccess, loadOdbrambeniSuccess, loadTimByIdSuccess, loadTimoviSuccess, loadVeznjaciSuccess, swapPlayers, swapPlayersSuccess, updateIgracSuccess, updateTimSuccess } from "./igrac.action";
import { Korisnik } from "../models/korisnik";

export const selectTimState = (state: TimState) => state.timovi;

export interface IgracState {
  igraci: Igrac[],
  pozicija:string,
}

export interface TimState {
  timovi: Tim[];
  trenutniTimID: number;
  igraciTeren: Igrac[];
  igraciZamena: Igrac[];
  selectedPlayerIdOut: number | null;
  selectedPlayerIdIn: number | null;
}

interface SelektovaniIgraciState {
  igracNaTerenId: number | null;
  igracNaKlupiId: number | null;
}


const initialStateTim: TimState = {
  timovi: [],
  trenutniTimID:0,
  igraciTeren: [],
  igraciZamena:[],
  selectedPlayerIdOut: 0,
  selectedPlayerIdIn: 0
};

export interface KorisnikState {
  trenutniKorisnik: Korisnik;
  ime:string,
  virtuelniNovac:number
}

const initialStateKorisnik: KorisnikState ={
  trenutniKorisnik: null;
  ime:"",
  virtuelniNovac:number
}

export const korisnikReducer = createReducer(
  initialState,
  on(buyIgrac, (state, { igrac }) => {
    const novaCena = state.trenutniKorisnik.virtuelniNovac - igrac.cena;

    if (novaCena < 0) {
      // Ako korisnik nema dovoljno novca, vratite trenutno stanje
      return { ...state };
    }

    const noviTrenutniKorisnik = {
      ...state.trenutniKorisnik,
      virtuelniNovac: novaCena,
    };

    const noviIgraci = [...state.igraci, igrac];

    const noviTim = state.tim.map((tim) => {
      if (tim.id === 1) {
        return {
          ...tim,
          igraciZamena: [...tim.igraciZamena, igrac.id],
        };
      }
      return tim;
    });

    const noviIgraciTransferLista = state.igraciTransferLista.filter(
      (i) => i.id !== igrac.id
    );

    return {
      ...state,
      trenutniKorisnik: noviTrenutniKorisnik,
      igraci: noviIgraci,
      tim: noviTim,
      igraciTransferLista: noviIgraciTransferLista,
    };
  }),
  // ... dodajte ostale case za ostale akcije ako je potrebno
);

export const timReducer = createReducer(
  initialStateTim,
  on(swapPlayersSuccess, (state, { igraciTeren, igraciZamena }) => ({
    ...state,
    igraciTeren,
    igraciZamena,
  })),
  on(loadTimoviSuccess, // znaci treba da def kako ce se stanje promeniti posle akcije loadTimoviSucces
     (state, { timovi }) =>
      ({ ...state, //destruktuiramo
         timovi: timovi })), //moze i samo timovi
  on(updateTimSuccess, state => ({ ...state })),
  on(swapPlayers, (state, { igracIzlaziId, igracUlaziId }) => ({
    ...state,
    selectedPlayerIdOut: igracIzlaziId,
    selectedPlayerIdIn: igracUlaziId
  })),
  on(swapPlayersSuccess, (state, { igraciTeren, igraciZamena }) => ({
    ...state,
    igraciTeren,
    igraciZamena,
    selectedPlayerIdOut: null,
    selectedPlayerIdIn: null
  })),

  on(loadTimByIdSuccess, (state, { tim,
     igraciTeren, igraciZamena }) => {
    return {
      ...state,
      timovi: state.timovi.map(postojeciTim =>
        postojeciTim.id === tim.id ? { ...tim } : postojeciTim
      ),
      igraciTerenIds: igraciTeren,
      igraciZamenaIds: igraciZamena
    };
  }),
  on(loadIgraciZamenaSuccess, (state, { igraciZamena }) => {
    return {
      ...state,
      igraciZamena: igraciZamena // Ažuriramo niz igrača zamena
    };
  }),
  on(loadIgraciTerenSuccess, (state, { igraciTeren }) => {
    return {
      ...state,
      igraciTeren: igraciTeren // Ažuriramo niz igrača zamena
    };
  }),
  on(loadGolmaniSuccess, (state, { golmani }) => {
    return {
      ...state,
      golmani: golmani
    };
  }),
  on(loadOdbrambeniSuccess, (state, { odbrambeni }) => {
    return {
      ...state,
      odbrambeni: odbrambeni
    };
  }),
  on(loadVeznjaciSuccess, (state, { veznjaci }) => {
    return {
      ...state,
      veznjaci: veznjaci
    };
  }),
  on(loadNapadaciSuccess, (state, { napadaci }) => {
    return {
      ...state,
      napadaci: napadaci
    };
  })
);

const initialStateIgraci: IgracState = {
  igraci: [],
  pozicija:""
};

export const igracReducer = createReducer(
  initialStateIgraci,
  on(loadIgraciSuccess,
     (state, { igraci }) =>
      ({ ...state,
         igraci: igraci })),
  // on(updateIgracSuccess, state => ({ ...state })),
  
  
);
