import { createSelector } from "@ngrx/store";
import { AppState } from "../app.state";
import { TimState } from "./igrac.reducer";

export const selectTimFeature = createSelector(
  (state: TimState) => state.timovi,
  (timovi) => timovi
);

export const selectTimState = createFeatureSelector<fromApp.TimState>('timovi');

export const selectTrenutniTim = createSelector(
  selectTimFeature, // Selektor koji vraća TimState
  (state: TimState, props: { timID: number }) => {
    return state.timovi.find(tim => tim.id === props.timID);
  }
);


