import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { Store } from '@ngrx/store';
import { Observable, of } from 'rxjs';
import { AppState } from 'src/app/app.state';
import { Igrac } from 'src/app/models/igrac';
import { Tim } from 'src/app/models/tim';
import * as fromApp from 'src/app/store/igrac.selector';
import * as TimActions from 'src/app/store/igrac.action';
import { FudbalskitimService } from 'src/app/services/fudbalskitim.service';

@Component({
  selector: 'app-fudbalskitim',
  templateUrl: './fudbalskitim.component.html',
  styleUrls: ['./fudbalskitim.component.css']
})
export class FudbalskitimComponent implements OnInit {
  trenutniTimID = 2; // hardcoded
  trenutniTim$: Observable<Tim | undefined> = of();
  igraciTerenIds$: Observable<number[]> = of([]);
  igraciZamenaIds$: Observable<number[]> = of([]);
  igraciZamena$: Observable<Igrac[]>= of([]);
  golmani$: Observable<Igrac[]>= of([]);
  odbrambeni$: Observable<Igrac[]>= of([]);
  veznjaci$: Observable<Igrac[]>= of([]);
  napadaci$: Observable<Igrac[]> = of([]);

  selectedPlayerIdOut: number | null = null;
  selectedPlayerIdIn: number | null = null;

  constructor(private store: Store<AppState>, private fudbalskitimService: FudbalskitimService) {}

  ngOnInit(): void {
    // this.trenutniTim$ = this.store.select(fromApp.selectTrenutniTim);
    this.igraciTerenIds$ = this.store.select(fromApp.selectIgraciTerenIds);
    this.igraciZamenaIds$ = this.store.select(fromApp.selectIgraciZamenaIds);
    this.igraciZamena$ = this.store.select(fromApp.selectIgraciZamena);
    this.golmani$ = this.store.select(fromApp.selectGolmani);
    this.odbrambeni$ = this.store.select(fromApp.selectOdbrambeni);
    this.veznjaci$ = this.store.select(fromApp.selectVeznjaci);
    this.napadaci$ = this.store.select(fromApp.selectNapadaci);
    console.log("AJDE DA VIDIMO: " + "PRVO " + this.igraciTerenIds$,"PRVO " + this.igraciZamenaIds$,this.igraciZamena$,this.golmani$,this.odbrambeni$,this.veznjaci$
      )

    this.store.dispatch(TimActions.loadTimById({ timID: this.trenutniTimID }));

  }

  clickedUlazi(playerId: number) {
    this.selectedPlayerIdOut = playerId;
  }

  clickedIzlazi(playerId: number) {
    this.selectedPlayerIdIn = playerId;
  }

  swapPlayers() {
    if (this.selectedPlayerIdOut !== null && this.selectedPlayerIdIn !== null) {
      this.store.dispatch(
        TimActions.swapPlayers({
          igracIzlaziId: this.selectedPlayerIdOut,
          igracUlaziId: this.selectedPlayerIdIn
        })
      );

      this.selectedPlayerIdOut = null;
      this.selectedPlayerIdIn = null;
    }
  }
}
<div class="football-field">
  <div class="teren" (click)="swapPlayers()">
    <div class="linija-tima">
      <div class="player" *ngFor="let igrac of golmani$ | async">
        <div class="player-circle" (click)="clickedIzlazi(igrac.id)">
          <div class="player-number">{{ igrac.id }}</div>
          <div class="player-name">{{ igrac.ime }}</div>
          <div class="player-rating">{{ igrac.ocena }}</div>
        </div>
      </div>
    </div>

    <div class="linija-tima">
      <div class="player" *ngFor="let igrac of odbrambeni$ | async">
        <div class="player-circle" (click)="clickedIzlazi(igrac.id)">
          <div class="player-number">{{ igrac.id }}</div>
          <div class="player-name">{{ igrac.ime }}</div>
          <div class="player-rating">{{ igrac.ocena }}</div>
        </div>
      </div>
    </div>

    <div class="linija-tima">
      <div class="player" *ngFor="let igrac of veznjaci$ | async">
        <div class="player-circle" (click)="clickedIzlazi(igrac.id)">
          <div class="player-number">{{ igrac.id }}</div>
          <div class="player-name">{{ igrac.ime }}</div>
          <div class="player-rating">{{ igrac.ocena }}</div>
        </div>
      </div>
    </div>

    <div class="linija-tima">
      <div class="player" *ngFor="let igrac of napadaci$ | async">
        <div class="player-circle" (click)="clickedIzlazi(igrac.id)">
          <div class="player-number">{{ igrac.id }}</div>
          <div class="player-name">{{ igrac.ime }}</div>
          <div class="player-rating">{{ igrac.ocena }}</div>
        </div>
      </div>
    </div>
  </div>

  <div class="zamene">
    <div class="player substitute" *ngFor="let igrac of igraciZamena$ | async">
      <div class="player-circle substitute-circle" (click)="clickedUlazi(igrac.id)">
        <div class="player-number">{{ igrac.id }}</div>
        <div class="player-name">{{ igrac.ime }}</div>
        <div class="player-rating">{{ igrac.ocena }}</div>
      </div>
    </div>
  </div>
</div>
