import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {
  form: FormGroup;

  constructor(
    private formBuilder: FormBuilder,
    private http:HttpClient,
    private router:Router)
  {
    this.form = this.formBuilder.group({
    ime:'',
    email:'',
    password:''
   });
  }
  ngOnInit(): void {

  }

  submit(): void{
    this.http.post('http://localhost:3000/api/register', this.form.getRawValue()).subscribe(() => 
      this.router.navigate(['/login'])
    );
  }
}
<main class="form-signin w-100 m-auto">
  <form [formGroup]="form" (submit)="submit()">
    <h1 class="h3 mb-3 fw-normal">Registruj se</h1>

    <input formControlName="ime" class="form-control" placeholder="Ime" required>

    <input formControlName="email" type="email" class="form-control" placeholder="Email" required>

    <input formControlName="password" type="password" class="form-control" placeholder="Password" required>

    <div *ngIf="form.invalid && (form.get('ime')?.dirty || form.get('email')?.dirty || form.get('password')?.dirty)"
         class="alert alert-danger">
      Sva polja moraju biti popunjena!
    </div>

    <button class="btn btn-primary w-100 py-2" type="submit">Registruj se</button>
  </form>
</main>
