import { Component } from '@angular/core';
import { Store } from '@ngrx/store';
import { Observable, map, switchMap } from 'rxjs';
import { AppState } from 'src/app/app.state';
import { Tim } from 'src/app/models/tim';
import { FudbalskitimService } from 'src/app/services/fudbalskitim.service';

@Component({
  selector: 'app-utakmica',
  templateUrl: './utakmica.component.html',
  styleUrls: ['./utakmica.component.css']
})
export class UtakmicaComponent {


  constructor(private store: Store<AppState>, private fudbalskitimService: FudbalskitimService) {
  }

  simulateMatch(protivnikId: number): Observable<Tim> {
    return this.getTimById(this.trenutniTimID).pipe(
      switchMap((nasTim) => {
        return this.getTimById(protivnikId).pipe(
          map((protivnik) => {
            // Proračunajte ukupne ocene igrača za oba tima
            const ukupnaOcenaNasihIgraca = nasTim.igraciTeren.reduce((acc, igrac) => acc + igrac.ocena, 0);
            const ukupnaOcenaProtivnikovihIgraca = protivnik.igraciTeren.reduce((acc, igrac) => acc + igrac.ocena, 0);

            // Utvrdite pobednika na osnovu ukupnih ocena
            let pobednik: Tim | null = null;
            if (ukupnaOcenaNasihIgraca > ukupnaOcenaProtivnikovihIgraca) {
              pobednik = nasTim;
            } else if (ukupnaOcenaNasihIgraca < ukupnaOcenaProtivnikovihIgraca) {
              pobednik = protivnik;
            }

            return pobednik;
          })
        );
      })
    );
  }

}
