import { Component, OnInit } from '@angular/core';
import { Observable, of } from 'rxjs';
import { Igrac } from 'src/app/models/igrac';
import { FudbalskitimService } from 'src/app/services/fudbalskitim.service';


@Component({
  selector: 'app-fudbalskitim',
  templateUrl: './fudbalskitim.component.html',
  styleUrls: ['./fudbalskitim.component.css']
})


export class FudbalskitimComponent implements OnInit {

  trenutniTimID = 1; //hardcodede
  trenutniTim:any;
  igraciTerenIds: number[] = [];
  igraciZamenaIds: number[] = [];
  igraciTeren: any[] = [];
  igraciZamena: any[] = [];


  golmani: any[] = [];
  odbrambeni: any[] = [];
  veznjaci: any[] = [];
  napadaci: any[] = [];


  // igrac$: Observable<Igrac[]> = of([]);

  constructor(private fudbalskitimService:FudbalskitimService)
  {}

  ngOnInit() : void {
    this.fudbalskitimService.getTimById(this.trenutniTimID).subscribe((tim) => 
    {
      this.trenutniTim = tim;
      this.igraciTerenIds = tim.igraciTeren;
      this.igraciZamenaIds = tim.igraciZamena;

      this.igraciTeren=this.igraciTerenIds.map((igracId:number) => this.fudbalskitimService.getIgracById(igracId));
      this.igraciZamena=this.igraciZamenaIds.map((igracId:number) => this.fudbalskitimService.getIgracById(igracId));

      // Dodajte deo za određivanje pozicija igracima
      this.igraciTeren.forEach((igrac: any) => {
        if (igrac.pozicija === 'golman') {
          igrac.pozicijaNaTerenu = 'golman';
        } else if (igrac.pozicija === 'odbrana') {
          igrac.pozicijaNaTerenu = 'odbrana';
        } else if (igrac.pozicija === 'sredina') {
          igrac.pozicijaNaTerenu = 'sredina';
        } else if (igrac.pozicija === 'napad') {
          igrac.pozicijaNaTerenu = 'napad';
        }
      });


    })
  }



  }

<!-- <ul *ngFor="let igrac of igrac$ | async">
  {{igrac.ime}}
  {{igrac.ocena}}
</ul> -->

<div class="football-field">
  <div class="teren">
    <div class="player" *ngFor="let igrac of golmani">
      <div class="player-circle golman-circle">
        <div class="player-number">{{ igrac.broj }}</div>
        <div class="player-name">{{ igrac.ime }}</div>
        <div class="player-rating">{{ igrac.ocena }}</div>
      </div>
    </div>

    <div class="player" *ngFor="let igrac of odbrambeni">
      <!-- ... Ostatak koda za prikaz odbrambenih igraca -->
    </div>

    <div class="player" *ngFor="let igrac of veznjaci">
      <!-- ... Ostatak koda za prikaz veznjaka -->
    </div>

    <div class="player" *ngFor="let igrac of napadaci">
      <!-- ... Ostatak koda za prikaz napadaca -->
    </div>
  </div>

  <div class="zamene">
    <div class="player substitute" *ngFor="let igrac of igraciZamena">
      <div class="player-circle substitute-circle">
        <div class="player-number">{{ igrac.broj }}</div>
        <div class="player-name">{{ igrac.ime }}</div>
        <div class="player-rating">{{ igrac.ocena }}</div>
      </div>
    </div>
  </div>
</div>



<div class="football-field">
  <div class="teren">
    <div class="player" *ngFor="let igrac of igraciTeren" >
      <div class="player-circle">
          <div class="player-number">{{ igrac.broj }}</div>
          <div class="player-name">{{ igrac.ime }}</div>
          <div class="player-rating">{{ igrac.ocena }}</div>
      </div>
    </div>
  </div>
  <div class="zamene">
    <div class="player substitute" *ngFor="let igrac of igraciZamena">
      <div class="player-circle substitute-circle">
        <div class="player-number">{{ igrac.broj }}</div>
        <div class="player-name">{{ igrac.ime }}</div>
        <div class="player-rating">{{ igrac.ocena }}</div>
      </div>
    </div>
  </div>
</div>
