import { createSelector } from '@ngrx/store';
import { TimState } from './igrac.reducer'; // Promenite putanju prema vašem tim.reducer fajlu

export const selectTrenutniTim = createSelector(
  selectTimState,
  (state: TimState) => state.timovi.find(tim => tim.id === trenutniTimID)
);

export const selectIgraciTerenIds = createSelector(
  selectTimState,
  (state: TimState) => state.timovi.find(tim => tim.id === trenutniTimID)?.igraciTeren || []
);

export const selectIgraciZamenaIds = createSelector(
  selectTimState,
  (state: TimState) => state.timovi.find(tim => tim.id === trenutniTimID)?.igraciZamena || []
);

export const selectIgraciZamena = createSelector(
  selectIgraciZamenaIds,
  selectIgraci,
  (zamenaIds, igraci) => zamenaIds.map(id => igraci.find(igrac => igrac.id === id))
);

// Slično za selectGolmani, selectOdbrambeni, selectVeznjaci i selectNapadaci
// Samo promenite pozivanje funkcija i filtere za svaku poziciju

export const selectGolmani = createSelector(
  selectIgraciTerenIds,
  selectIgraci,
  (golmaniIds, igraci) => golmaniIds.map(id => igraci.find(igrac => igrac.id === id))
);

export const selectOdbrambeni = createSelector(
  selectIgraciTerenIds,
  selectIgraci,
  (golmaniIds, igraci) => golmaniIds.map(id => igraci.find(igrac => igrac.id === id))
);

export const selectVeznjaci = createSelector(
  selectIgraciTerenIds,
  selectIgraci,
  (golmaniIds, igraci) => golmaniIds.map(id => igraci.find(igrac => igrac.id === id))
);

export const selectNapadaci = createSelector(
  selectIgraciTerenIds,
  selectIgraci,
  (golmaniIds, igraci) => golmaniIds.map(id => igraci.find(igrac => igrac.id === id))
);



