import { createReducer, on } from "@ngrx/store";
import { Igrac } from "../models/igrac";
import { swapPlayers } from "./igrac.action";
import * as Actions from './igrac.action';
import { Tim } from "../models/tim";

export interface IgracState {
  selectedPlayerIdOut: number;
  selectedPlayerIdIn: number;
}

export const initialState: IgracState = {
  selectedPlayerIdOut: 0,
  selectedPlayerIdIn: 0,
};

export const timReducer = createReducer(
  initialState,
  on(Actions.swapPlayers, (state, { selectedPlayerIdOut, selectedPlayerIdIn }) => {
    console.log(selectedPlayerIdOut, selectedPlayerIdIn)
    const igraciTerenIds = [...state.igraciTeren]; // Kreira novi niz na osnovu postojećeg niza
    const igraciZamenaIds = [...state.igraciZamena]; // Kreira novi niz na osnovu postojećeg niza

    // Pronalazi indekse igrača u nizu ID-jeva
    const indexTerena = igraciTerenIds.indexOf(selectedPlayerIdIn);
    const indexKlupe = igraciZamenaIds.indexOf(selectedPlayerIdOut);

    // Ako su indeksi pronađeni, vrši zamenu
    if (indexTerena !== -1 && indexKlupe !== -1) {
      // Zamena ID-jeva u nizovima
      [igraciTerenIds[indexTerena], igraciZamenaIds[indexKlupe]] = [igraciZamenaIds[indexKlupe], igraciTerenIds[indexTerena]];
    }

    // Vraća novo stanje sa izmenjenim nizovima ID-jeva
    return {
      ...state,
      igraciTerenIds,
      igraciZamenaIds
    };
  })
);
