import { createSelector } from '@ngrx/store';
import { TimState, IgracState } from './igrac.reducer'; // Promenite putanju prema vašem tim.reducer fajlu

import { AppState } from '../app.state';
import {Igrac} from "src/app/models/igrac"

export const selectTimState = createSelector(
  (state: AppState) => state.timovi,
  (timovi) => timovi
);

export const selectAllIgraci = createSelector(
  (state: AppState) => state.igraci.igraci,
  (igraci) => igraci
);

export const selectTrenutniTim = createSelector(
  selectTimState,
  (state: TimState) => {
    const tim = state.timovi.find(tim => tim.id === state.trenutniTimID);
    return tim ? tim : null;
  }
);

export const selectIgraciTerenIds = createSelector(
  selectTimState,
  (state: TimState) => state.igraciTeren
);

export const selectIgraciZamenaIds = createSelector(
  selectTimState,
  (state: TimState) => state.igraciZamena
);

export const selectIgraciTeren = createSelector(
  selectTimState,
  (state: TimState) => state.igraciTeren // Koristimo direktno niz igrača zamena iz stanja
);

export const selectIgraciZamena = createSelector(
  selectTimState,
  (state: TimState) => state.igraciZamena // Koristimo direktno niz igrača zamena iz stanja
);

export const selectGolmani = createSelector(
  selectTimState,
  selectAllIgraci,
  (state: TimState, igraci: Igrac[]) => {
    const golmaniIds = igraci
      .filter(igrac => state.igraciTeren.includes(igrac.id) && igrac.pozicija === 'golman')
      .map(igrac => igrac.id); // Dobijamo niz ID-jeva golmana
    
    const golmani = igraci.filter(igrac => golmaniIds.includes(igrac.id));
    return golmani;
  }
);



