import { Injectable } from '@angular/core';
import { Actions, createEffect, ofType } from '@ngrx/effects';
import { of } from 'rxjs';
import { catchError, map, mergeMap } from 'rxjs/operators';
import { FudbalskitimService } from 'src/app/services/fudbalskitim.service';
import { swapPlayers, swapPlayersSuccess} from 'src/app/store/igrac.action';


@Injectable()
export class TimEffects {
  constructor(
    private actions$: Actions,
    private fudbalskitimService: FudbalskitimService
  ) {}

  swapPlayers$ = createEffect(() =>
    this.actions$.pipe(
      ofType(swapPlayers),
      mergeMap(({ selectedPlayerIdOut, selectedPlayerIdIn }) =>
        this.fudbalskitimService.swapPlayers(selectedPlayerIdOut, selectedPlayerIdIn).pipe(
          map(() => swapPlayersSuccess()), // Emitujte akciju za uspešno zamenjene igrače
          catchError(() => of()) // Emitujte akciju za neuspešnu zamenu igrača
        )
      )
    )
  );
}
