import { Component, OnInit } from '@angular/core';
import { Store } from '@ngrx/store';
import { AppState } from 'src/app/app.state';
import { Korisnik } from 'src/app/models/korisnik';
import { FudbalskitimService } from 'src/app/services/fudbalskitim.service'
import { KorisnikService } from 'src/app/services/korisnik.service';
import { buyIgrac } from 'src/app/store/igrac.action';
import { selectTrenutniKorisnik } from 'src/app/store/igrac.selector';

@Component({
  selector: 'app-transferi',
  templateUrl: './transferi.component.html',
  styleUrls: ['./transferi.component.css']
})
export class TransferiComponent implements OnInit {
  igraciTransferLista: any[] = [];
  trenutniKorisnik$: Observable<Korisnik | null>;

  constructor(private store: Store<AppState>, private fudbalskiTimService: FudbalskitimService, private korisnikService: KorisnikService) {
    this.trenutniKorisnik$ = this.store.select(selectTrenutniKorisnik);
  }

  ngOnInit() {
    this.fudbalskiTimService.getIgraciTransferLista().subscribe(data => {
      this.igraciTransferLista = data;
    });
  }

  kupiIgraca(igrac: any) {
    this.trenutniKorisnik$.pipe(
      take(1) // Pretplata će se automatski poništiti nakon emitovanja prvog podatka
    ).subscribe(trenutniKorisnik => {
      if (trenutniKorisnik?.virtuelniNovac && trenutniKorisnik.virtuelniNovac >= igrac.cena) {
        this.store.dispatch(buyIgrac({ igrac }));
      } else {
        console.log('Nemate dovoljno novca za kupovinu.');
      }
    });
  }
}
<h2>Transferi</h2>

<div class="igrac-kolona" *ngFor="let igrac of igraciTransferLista">
  <div class="igrac-podaci">
    <p>Ime: {{ igrac.ime }}</p>
    <p>Ocena: {{ igrac.ocena }}</p>
    <p>Pozicija: {{ igrac.pozicija }}</p>
    <p>Cena: {{ igrac.cena }}</p>
  </div>
  <button class="kupi-button" (click)="kupiIgraca(igrac)"> Kupi igrača</button>
</div>
