import { Injectable } from '@angular/core';
import { JwtService } from './jwt.service';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  constructor(private jwtService: JwtService) {}

  isLoggedIn(): boolean {
    return !!this.jwtService.getToken();
  }

  getUserId(): number | null {
