    // Uzmi ID korisnika iz JWT tokena
    const token = this.jwtService.getToken();
    console.log("Sad Ce TOKEN")
    console.log(token);
    if (!token) {
      return null;
    }

    // Dekodiraj JWT da bi dobili podatke o korisniku
    const decodedToken = atob(token.split('.')[1]); // Dekodiran drugi deo JWT
    const parsedToken = JSON.parse(decodedToken);

    return parsedToken.id; // Promeni "userId" u "id" ako je to pravo ime polja u JWT tokenima
  }
}
