<div class="teren">
  <div class="player" *ngFor="let igrac of igraciTeren">
    <div class="player-info">
      <div class="player-number">{{ igrac.broj }}</div>
      <div class="player-name">{{ igrac.ime }}</div>
      <div class="player-rating">{{ igrac.ocena }}</div>
    </div>
  </div>
</div>
<div class="zamene">
  <div class="player substitute" *ngFor="let igrac of igraciZamena">
    <div class="player-info">
      <div class="player-number">{{ igrac.broj }}</div>
      <div class="player-name">{{ igrac.ime }}</div>
      <div class="player-rating">{{ igrac.ocena }}</div>
    </div>
  </div>
</div>
