import { createAction, props } from "@ngrx/store";
import { Tim } from "../models/tim";

export const loadTimovi = createAction('Load Timovi');
export const loadTimoviSuccess = createAction(
  'Load Timovi Success',
  props<{ timovi: Tim[] }>()
);
export const swapPlayers = createAction(
  'Swap Players',
  props<{ selectedPlayerIdOut: number; selectedPlayerIdIn: number }>()
);

export const swapPlayersSuccess = createAction('Swap Players Success');
export const swapPlayersFailure = createAction(
  'Swap Players Failure',
  props<{ error: any }>()
);
