import { createSelector } from '@ngrx/store';
import { TimState, IgracState } from './igrac.reducer'; // Promenite putanju prema vašem tim.reducer fajlu

import { AppState } from '../app.state';
import {Igrac} from "src/app/models/igrac"

export const selectTimState = createSelector(
  (state: AppState) => state.timovi,
  (timovi) => timovi
);

export const selectAllIgraci = createSelector(
  (state: IgracState) => state.igraci,
  (igraci) => igraci
);

export const selectTrenutniTim = createSelector(
  selectTimState,
  (state: TimState) => {
    const tim = state.timovi.find(tim => tim.id === state.trenutniTimID);
    return tim ? tim : null;
  }
);

export const selectIgraciTerenIds = createSelector(
  selectTimState,
  (state: TimState) => state.igraciTerenIds
);

export const selectIgraciZamenaIds = createSelector(
  selectTimState,
  (state: TimState) => state.igraciZamenaIds
);

export const selectIgraciTeren = createSelector(
  selectTimState,
  selectAllIgraci,
  (state: TimState, igraci: Igrac[]) => {
    const igraciTeren = igraci.filter(igrac => state.igraciTerenIds.includes(igrac.id));
    return igraciTeren;
  }
);

export const selectIgraciZamena = createSelector(
  selectTimState,
  selectAllIgraci,
  (state: TimState, igraci: Igrac[]) => {
    const igraciZamena = igraci.filter(igrac => state.igraciZamenaIds.includes(igrac.id));
    return igraciZamena;
  }
);

export const selectGolmani = createSelector(
  selectTimState,
  selectAllIgraci,
  (state: TimState, igraci: Igrac[]) => {
    const golmani = igraci.filter(igrac => state.igraciTerenIds.includes(igrac.id) && igrac.pozicija === 'golman');
    return golmani;
  }
);

export const selectOdbrambeni = createSelector(
  selectTimState,
  selectAllIgraci,
  (state: TimState, igraci: Igrac[]) => {
    const odbrambeni = igraci.filter(igrac => state.igraciTerenIds.includes(igrac.id) && igrac.pozicija === 'odbrana');
    return odbrambeni;
  }
);

export const selectVeznjaci = createSelector(
  selectTimState,
  selectAllIgraci,
  (state: TimState, igraci: Igrac[]) => {
    const veznjaci = igraci.filter(igrac => state.igraciTerenIds.includes(igrac.id) && igrac.pozicija === 'sredina');
    return veznjaci;
  }
);

export const selectNapadaci = createSelector(
  selectTimState,
  selectAllIgraci,
  (state: TimState, igraci: Igrac[]) => {
    const napadaci = igraci.filter(igrac => state.igraciTerenIds.includes(igrac.id) && igrac.pozicija === 'napad');
    return napadaci;
  }
);
