import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Igrac } from '../models/igrac';
import { Tim } from '../models/tim';
import { environment } from 'src/environments/environment';
import { Observable, forkJoin, switchMap } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class FudbalskitimService {

  constructor(private httpClient: HttpClient) { }



  getIgraciTransferLista() {
    return this.httpClient.get<Igrac[]>
    (environment.api + "/igraciTransferLista");
  }

  dodajIgraca(igrac: Igrac) {
    return this.httpClient.post<Igrac>(`${environment.api}/igrac/`, igrac);
  }

  ukloniIgracaSaTransferListe(igracId: number) {
    return this.httpClient.delete<void>(`${environment.api}/igraciTransferLista/${igracId}`);
  }

  dodajIgracaZamenama(igracZamenaID: number, timId: number) {
    // Dohvatanje trenutnog tima sa servera
    return this.httpClient.get<Tim>(environment.api + `/tim/${timId}`).pipe(
      switchMap((tim: Tim) => {
        tim.igraciZamena.push(igracZamenaID);

        // Slanje ažuriranih podataka na server radi ažuriranja tima
        return this.httpClient.put<Tim>(environment.api + `/tim/${timId}`, tim);
      })
    );
  }




  ngOnInit(): void {
  }

  getSviIgraci(){
    return this.httpClient.get<Igrac[]>(environment.api + "/igrac");
  }

  getTimById(timId: number) {
    return this.httpClient.get<any>(environment.api + `/tim/${timId}`);
  }

  getSviTimovi(){
    return this.httpClient.get<Tim[]>(environment.api + "/tim");
  }

  getIgracById(igracId: number) {
    return this.httpClient.get<Igrac>(environment.api + `/igrac/${igracId}`);
  }

  getIgraciByIds(igraciIds: number[]): Observable<Igrac[]> {
    const requests: Observable<Igrac>[] = igraciIds.map(id => this.getIgracById(id));
    return forkJoin(requests);
  }


  getIgraciByPozicija(pozicija: string, timId: number) {
    return this.httpClient.get<Igrac[]>(environment.api + `/igrac?pozicija=${pozicija}&timId=${timId}`);
  }

  updateTimIgraci(timId: number, igraciTerenIds: number[], igraciZamenaIds: number[], tim:Tim): Observable<any> {
    const url = `${environment.api}/tim/${timId}`;
    const updatedData = {
      ...tim,
      igraciTeren: igraciTerenIds,
      igraciZamena: igraciZamenaIds
    };

    return this.httpClient.put(url, updatedData);
  }

}
