<div class="utakmica-container">
  <div class="timovi-container">
    <div class="nas-tim">
      <h2>Moj tim</h2>
      <div>{{ trenutniTim?.naziv }}</div>
    </div>
    <div class="svi-timovi">
      <h2>Izaberi protivnika: </h2>
      <ul>
        <div
          *ngFor="let tim of sviTimovi"
          (click)="simulateMatch(tim.id)"
          class="timUtakmica"
        >
          {{ tim.naziv }}
        </div>
      </ul>
    </div>
  </div>
  <div *ngIf="winner" class="winner-container">
    <h2>Pobednik</h2>
    <h5>{{ winner.naziv }}</h5>
    <p>Rezultat {{brGolovaMi}} : {{brGolovaProtivnik}}</p>
  </div>
</div>
