import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { Observable, filter, forkJoin, of } from 'rxjs';
import { Igrac } from 'src/app/models/igrac';
import { FudbalskitimService } from 'src/app/services/fudbalskitim.service';


@Component({
  selector: 'app-fudbalskitim',
  templateUrl: './fudbalskitim.component.html',
  styleUrls: ['./fudbalskitim.component.css']
})



export class FudbalskitimComponent implements OnInit {

  trenutniTimID = 1; //hardcodede
  trenutniTim: any;
  igraciTerenIds: number[] = [];
  igraciZamenaIds: number[] = [];
  igraciTeren: any[] = [];
  igraciZamena: any[] = [];

  golmani: any[] = [];
  odbrambeni: any[] = [];
  veznjaci: any[] = [];
  napadaci: any[] = [];


  // igrac$: Observable<Igrac[]> = of([]);

  constructor(private fudbalskitimService: FudbalskitimService) { }

  ngOnInit(): void {
    // this.igrac$ = this.fudbalskitimService.getSviIgraci();
    this.fudbalskitimService.getTimById(this.trenutniTimID).subscribe((tim) => {

      this.trenutniTim = tim;
      this.igraciTerenIds = tim.igraciTeren;
      this.igraciZamenaIds = tim.igraciZamena;


      const requests = this.igraciZamenaIds.map((igracId: number) =>
      this.fudbalskitimService.getIgracById(igracId)
    );

    forkJoin(requests).subscribe((igraci: Igrac[][]) => {
      this.igraciZamena = igraci.flat(); //flat - ovde ce spojimo nizove u jedan niz objekata
    });

    //koristimo FORKJOIN za istovremeno pozivanje vise zahteva, i ceka se da se svi zahtevi zavrse pre nego sto se obrade rezultati

    this.fudbalskitimService.getIgraciByPozicija('golman', this.trenutniTimID).subscribe((igraci: Igrac[]) => {
      this.golmani = igraci.filter(igrac => this.igraciTerenIds.includes(igrac.id));
    });

      this.fudbalskitimService.getIgraciByPozicija('odbrana', this.trenutniTimID).subscribe((igraci: Igrac[]) => {
        this.odbrambeni = igraci.filter(igrac => this.igraciTerenIds.includes(igrac.id)); // dodelice se samo oni ciji se id nalazi u igraciTerenIds!
      });

      this.fudbalskitimService.getIgraciByPozicija('sredina', this.trenutniTimID).subscribe((igraci: Igrac[]) => {
        this.veznjaci = igraci.filter(igrac => this.igraciTerenIds.includes(igrac.id));
      });
      this.fudbalskitimService.getIgraciByPozicija('napad', this.trenutniTimID).subscribe((igraci: Igrac[]) => {
        this.napadaci = igraci.filter(igrac => this.igraciTerenIds.includes(igrac.id));
      });
    })


  }
  @Output() onClick:EventEmitter<Igrac>=new EventEmitter<Igrac>();

  selectedPlayerIdOut: number | null = null;
  selectedPlayerIdOut: number | null = null;

  clickedUlazi(playerId: number)
  {
    this.selectedPlayerIdOut = playerId;
  }

  clickedIzlazi(playerId: number)
  {
    this.selectedPlayerIdIn = playerId;
  }

}



<div class="football-field">
  <div class="teren">
    <div class="linija-tima">
      <div class="player" *ngFor="let igrac of golmani">
        <div class="player-circle ">
          <div class="player-number">{{ igrac.id }}</div>
          <div class="player-name">{{ igrac.ime }}</div>
          <div class="player-rating">{{ igrac.ocena }}</div>
        </div>
      </div>
    </div>

    <div class="linija-tima">
      <div class="player odbrambena-linija" *ngFor="let igrac of odbrambeni">
        <div class="player-circle ">
          <div class="player-number">{{ igrac.id }}</div>
          <div class="player-name">{{ igrac.ime }}</div>
          <div class="player-rating">{{ igrac.ocena }}</div>
        </div>
      </div>
    </div>

    <div class="linija-tima">
      <div class="player" *ngFor="let igrac of veznjaci">
        <div class="player-circle vezna-linija">
          <div class="player-number">{{ igrac.id }}</div>
          <div class="player-name">{{ igrac.ime }}</div>
          <div class="player-rating">{{ igrac.ocena }}</div>
        </div>
      </div>
    </div>

    <div class="linija-tima">
      <div class="player" *ngFor="let igrac of napadaci">
        <div class="player-circle napadacka-linija">
          <div class="player-number">{{ igrac.id }}</div>
          <div class="player-name">{{ igrac.ime }}</div>
          <div class="player-rating">{{ igrac.ocena }}</div>
        </div>
      </div>
    </div>
  </div>


  <div class="zamene">
    <div class="player substitute" *ngFor="let igrac of igraciZamena">
      <div class="player-circle substitute-circle" (click)="clickedUlazi(igrac.id)">
        <div class="player-number">{{ igrac.id }}</div>
        <div class="player-name">{{ igrac.ime }}</div>
        <div class="player-rating">{{ igrac.ocena }}</div>
      </div>
    </div>
  </div>
</div>
