import { createReducer, on } from "@ngrx/store";
import { Igrac } from "../models/igrac";
import { Tim } from "../models/tim";
import { loadGolmaniSuccess, loadIgraciSuccess, loadIgraciTerenSuccess, loadIgraciZamenaSuccess, loadNapadaciSuccess, loadOdbrambeniSuccess, loadTimByIdSuccess, loadTimoviSuccess, loadVeznjaciSuccess, swapPlayers, swapPlayersSuccess, updateIgracSuccess, updateTimSuccess } from "./igrac.action";

export const selectTimState = (state: TimState) => state.timovi;

export interface IgracState {
  igraci: Igrac[],
  pozicija:string,
}

export interface TimState {
  timovi: Tim[];
  trenutniTimID: number;
  igraciTeren: Igrac[];
  igraciZamena: Igrac[];
}

interface SelektovaniIgraciState {
  igracNaTerenId: number | null;
  igracNaKlupiId: number | null;
}


const initialStateTim: TimState = {
  timovi: [],
  trenutniTimID:0,
  igraciTeren: [],
  igraciZamena:[],
};

export const timReducer = createReducer(
  initialStateTim,
  on(swapPlayersSuccess, (state, { igracIzlaziId, igracUlaziId }) => {
    const noviIgraciTeren = state.igraciTeren.map(igrac => {
      if (igrac.id === igracIzlaziId) {
        // Igrač izlazi, postaviti na undefined
        return undefined;
      } else if (igrac.id === igracUlaziId) {
        // Igrač ulazi, pronaći njegove detalje i postaviti na njega
        return igraci.find(igrac => igrac.id === igracUlaziId);
      } else {
        return igrac;
      }
    });
  
    const noviIgraciZamena = state.igraciZamena.map(igrac => {
      if (igrac.id === igracUlaziId) {
        // Igrač ulazi, postaviti na undefined
        return undefined;
      } else if (igrac.id === igracIzlaziId) {
        // Igrač izlazi, pronaći njegove detalje i postaviti na njega
        return igraci.find(igrac => igrac.id === igracIzlaziId);
      } else {
        return igrac;
      }
    });
  
    return {
      ...state,
      igraciTeren: noviIgraciTeren,
      igraciZamena: noviIgraciZamena
    };
  })
  on(loadTimoviSuccess, // znaci treba da def kako ce se stanje promeniti posle akcije loadTimoviSucces
     (state, { timovi }) =>
      ({ ...state, //destruktuiramo
         timovi: timovi })), //moze i samo timovi
  on(updateTimSuccess, state => ({ ...state })),
  on(swapPlayers, (state, { igracIzlaziId, igracUlaziId }) => {
    const updatedTimovi = state.timovi.map(tim => {
      const igracIzlaziIndex = tim.igraciTeren.indexOf(igracIzlaziId);
      const igracUlaziIndex = tim.igraciZamena.indexOf(igracUlaziId);

      // Ako su pronađeni, vršimo zamenu u nizovima
      if (igracIzlaziIndex !== -1 && igracUlaziIndex !== -1) {
        tim.igraciTeren[igracIzlaziIndex] = igracUlaziId;
        tim.igraciZamena[igracUlaziIndex] = igracIzlaziId;
      }

      return tim;
    });
    // console.log("OVO JE IZ TIM REDUCERA")
    // console.log(state,updatedTimovi);
    return { ...state, timovi: updatedTimovi };
  }),
  on(loadTimByIdSuccess, (state, { tim,
     igraciTeren, igraciZamena }) => {
    return {
      ...state,
      timovi: state.timovi.map(postojeciTim =>
        postojeciTim.id === tim.id ? { ...tim } : postojeciTim
      ),
      igraciTerenIds: igraciTeren,
      igraciZamenaIds: igraciZamena
    };
  }),
  on(loadIgraciZamenaSuccess, (state, { igraciZamena }) => {
    return {
      ...state,
      igraciZamena: igraciZamena // Ažuriramo niz igrača zamena
    };
  }),
  on(loadIgraciTerenSuccess, (state, { igraciTeren }) => {
    return {
      ...state,
      igraciTeren: igraciTeren // Ažuriramo niz igrača zamena
    };
  }),
  on(loadGolmaniSuccess, (state, { golmani }) => {
    return {
      ...state,
      golmani: golmani
    };
  }),
  on(loadOdbrambeniSuccess, (state, { odbrambeni }) => {
    return {
      ...state,
      odbrambeni: odbrambeni
    };
  }),
  on(loadVeznjaciSuccess, (state, { veznjaci }) => {
    return {
      ...state,
      veznjaci: veznjaci
    };
  }),
  on(loadNapadaciSuccess, (state, { napadaci }) => {
    return {
      ...state,
      napadaci: napadaci
    };
  })
);

const initialStateIgraci: IgracState = {
  igraci: [],
  pozicija:""
};

export const igracReducer = createReducer(
  initialStateIgraci,
  on(loadIgraciSuccess,
     (state, { igraci }) =>
      ({ ...state,
         igraci: igraci })),
  // on(updateIgracSuccess, state => ({ ...state })),
  
  
);
