import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root',
})
export class JwtService {
  constructor() {}

  // Funkcija za dobijanje JWT tokena iz kolacica
  getToken(): string | null {
    return this.getCookie('jwt'); // Pretpostavljamo da ćemo koristiti funkciju getCookie za čitanje kolacica
  }

  // Dodajte ovde funkciju za čitanje kolacica (koristimo "document.cookie" za to)
  private getCookie(name: string): string | null {
    const value = `; ${document.cookie}`;
    const parts = value.split(`; ${name}=`);
    if (parts.length === 2) return parts.pop()?.split(';').shift() || null;
    return null;
  }
}
