import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Korisnik } from '../models/korisnik';
import { environment } from 'src/environments/environment';

@Injectable({
  providedIn: 'root'
})
export class KorisnikService {

  constructor(private httpClient: HttpClient) { }

  getKorisnikById(korisnikId: number) {
    return this.httpClient.get<Korisnik>(environment.api + `/korisnici/${korisnikId}`);
  }

  dodajIgracaUTim(igrac: Igrac) {
    // Ovde šaljemo POST zahtev sa podacima o igracu koji se dodaje
    return this.httpClient.post<Igrac>(`${environment.api}/dodajIgracaUTim`, igrac);
  }

  ukloniIgracaSaTransferListe(igracId: number): Observable<void> {
    // Ovde šaljemo DELETE zahtev za uklanjanje igrača sa transfer liste
    return this.httpClient.delete<void>(`${environment.api}/ukloniIgracaSaTransferListe/${igracId}`);
  }

}
