<div class="football-field">
  <div class="teren">
    <!-- Rasporedjeni igraci na terenu -->
    <div class="player-circle golman" *ngFor="let igrac of golmani">
      <div class="player-number">{{ igrac.broj }}</div>
      <div class="player-name">{{ igrac.ime }}</div>
      <div class="player-rating">{{ igrac.ocena }}</div>
    </div>
    <div class="odbrana-line">
      <div class="player-circle odbrana" *ngFor="let igrac of odbrambeni">
        <div class="player-number">{{ igrac.broj }}</div>
        <div class="player-name">{{ igrac.ime }}</div>
        <div class="player-rating">{{ igrac.ocena }}</div>
      </div>
    </div>
    <div class="sredina-line">
      <div class="player-circle sredina" *ngFor="let igrac of veznjaci">
        <div class="player-number">{{ igrac.broj }}</div>
        <div class="player-name">{{ igrac.ime }}</div>
        <div class="player-rating">{{ igrac.ocena }}</div>
      </div>
    </div>
    <div class="napad-line">
      <div class="player-circle napad" *ngFor="let igrac of napadaci">
        <div class="player-number">{{ igrac.broj }}</div>
        <div class="player-name">{{ igrac.ime }}</div>
        <div class="player-rating">{{ igrac.ocena }}</div>
      </div>
    </div>
  </div>
  <div class="zamene">
    <!-- Lista zamena -->
    <div class="player-circle player substitute" *ngFor="let igrac of igraciZamena">
      <div class="player-number">{{ igrac.broj }}</div>
      <div class="player-name">{{ igrac.ime }}</div>
      <div class="player-rating">{{ igrac.ocena }}</div>
    </div>
  </div>
</div>





