import { Component, OnInit } from '@angular/core';
import { FudbalskitimService } from 'src/app/services/fudbalskitim.service'

@Component({
  selector: 'app-transferi',
  templateUrl: './transferi.component.html',
  styleUrls: ['./transferi.component.css']
})
export class TransferiComponent implements OnInit {
  igraciTransferLista: any[] = [];

  constructor(private fudbalskiTimService: FudbalskitimService) {}

  ngOnInit() {
    this.fudbalskiTimService.getIgraciTransferLista().subscribe(data => {
      this.igraciTransferLista = data;
    });
  }

  kupiIgraca(igracId: number, cena: number) {
    if (this.trenutniKorisnik.virtuelniNovac >= cena) {
      this.trenutniKorisnik.virtuelniNovac -= cena;
      // Implementirajte logiku za dodavanje igrača u tim i zamenu, te brisanje iz transfer liste
    } else {
      console.log("Nemate dovoljno virtuelnog novca.");
    }
  }
}
<h2>Transferi</h2>

<div class="igrac-kolona" *ngFor="let igrac of igraciTransferLista">
  <div class="igrac-podaci">
    <p>Ime: {{ igrac.ime }}</p>
    <p>Ocena: {{ igrac.ocena }}</p>
    <p>Pozicija: {{ igrac.pozicija }}</p>
    <p>Cena: {{ igrac.cena }}</p>
  </div>
  <button (click)="kupiIgraca(igrac.id, igrac.cena)" class="kupi-button"> >Kupi igrača</button>
</div>
