import { Component } from '@angular/core';
import { Store } from '@ngrx/store';
import { AppState } from 'src/app/app.state';
import { Igrac } from 'src/app/models/igrac';
import { Tim } from 'src/app/models/tim';
import { FudbalskitimService } from 'src/app/services/fudbalskitim.service';

import { loadTimById, loadTimovi } from 'src/app/store/igrac.action';

@Component({
  selector: 'app-utakmica',
  template: `
    <button (click)="simulateMatch()">Igraj utakmicu</button>
    <div *ngIf="winner">
      Pobednik: {{ winner.naziv }}
    </div>
  `,
})
export class UtakmicaComponent {
  trenutniTimID: number;
  winner: Tim | null = null;

  constructor(
    private store: Store<AppState>,
    private fudbalskitimService: FudbalskitimService
  ) {
    this.trenutniTimID = 1;
  }

  ngOnInit() {
    this.store.dispatch(loadTimovi()); // Učitavanje svih timova
    this.store.dispatch(loadTimById({ timID: this.trenutniTimID })); // Učitavanje trenutnog tima
  }

  simulateMatch() {
    this.fudbalskitimService.getTimById(this.trenutniTimID).subscribe((nasTim) => {
      this.fudbalskitimService.getSviTimovi().subscribe((timovi) => {
        // Proračunajte ukupne ocene igrača za oba tima
        const ukupnaOcenaNasihIgraca = nasTim.igraciTeren.reduce((acc:number, igrac:Igrac) => acc + igrac.ocena, 0);
        const protivnik = timovi.find((tim) => tim.id !== this.trenutniTimID);
        if (protivnik) {
          const ukupnaOcenaProtivnikovihIgraca = protivnik.igraciTeren.reduce((acc, igrac) => acc + igrac.ocena, 0);

          // Utvrdite pobednika na osnovu ukupnih ocena
          if (ukupnaOcenaNasihIgraca > ukupnaOcenaProtivnikovihIgraca) {
            this.winner = nasTim;
          } else if (ukupnaOcenaNasihIgraca < ukupnaOcenaProtivnikovihIgraca) {
            this.winner = protivnik;
          }
        }
      });
    });
  }
}
