import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { JwtService } from 'src/app/services/jwt.service';


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  form: FormGroup;
  neuspesnoLogovanje: boolean = false;

  constructor(private formBuilder: FormBuilder,
    private http:HttpClient,
    private router:Router,
    private jwtService: JwtService,
    )
  {
    this.form = this.formBuilder.group({
      email:'',
      password:''
     });
  }

  ngOnInit(): void {

  }

  submit(): void {
    this.http.post('http://localhost:3000/api/login', this.form.getRawValue(), { withCredentials: true })
      .subscribe((response: any) => {
        if (response && response.message === 'success') {
          // Token je uspešno dobijen, preusmeri korisnika na željenu stranicu
          const token = this.jwtService.getToken(); // Pretpostavljajući da imate getToken funkciju u JwtService
          if (token) {
            // Dekodiranje tokena
            const decodedToken = atob(token.split('.')[1]);
            const parsedToken = JSON.parse(decodedToken);
            const userId = parsedToken.id; // Pretpostavljajući da je ID polje u tokenima "id"

            // Postaviti userId u AppComponent
            this.router.navigate(['']);
          }
        }
        else {
          this.neuspesnoLogovanje = true;
        }
      });
  }

}
<main class="form-signin">
  <form [formGroup]="form" (ngSubmit)="submit()">

    <h1 class="h3 mb-3 fw-normal">Prijavi se</h1>

    <input formControlName="email" type="email" class="form-control" placeholder="Email">

    <input formControlName="password" type="password" class="form-control" placeholder="Password">




    <button class="btn btn-primary w-100 py-2" type="submit">Prijavi se</button>

  </form><div *ngIf="neuspesnoLogovanje" class="neuspesno-logovanje-poruka">
      Lose ste uneli email/lozinku, pokusajte ponovo.
    </div>
</main>
