import { createReducer, on } from "@ngrx/store";
import { Igrac } from "../models/igrac";
import { Tim } from "../models/tim";
import { loadIgraciSuccess, loadTimoviSuccess, swapPlayers, updateIgracSuccess, updateTimSuccess } from "./igrac.action";


interface IgracState {
  igraci: Igrac[];
}

interface TimState {
  timovi: Tim[];
  igraciTeren: [],
  igraciZamena: []
}

interface SelektovaniIgraciState {
  igračNaTerenId: number | null;
  igračNaKlupiId: number | null;
}

const initialStateTim: TimState = {
  timovi: [],
  igraciTeren: [],
  igraciZamena:[]
};

export const timReducer = createReducer(
  initialStateTim,
  on(loadTimoviSuccess, // znaci treba da def kako ce se stanje promeniti posle akcije loadTimoviSucces
     (state, { timovi }) =>
      ({ ...state, //destruktuiramo
         timovi: timovi })), //moze i samo timovi
  on(updateTimSuccess, state => ({ ...state })),
);

const initialStateIgraci: IgracState = {
  igraci: []
};

export const igracReducer = createReducer(
  initialStateIgraci,
  on(loadIgraciSuccess,
     (state, { igraci }) =>
      ({ ...state,
         igraci: igraci })),
  on(updateIgracSuccess, state => ({ ...state })),
  on(swapPlayers, (state, action) => {
    const igraci = [...state.igraci]; // Kopija niza igrača

    const indexIzlazi = igraci.findIndex(igrac => igrac.id === action.igracIzlaziId);
    const indexUlazi = igraci.findIndex(igrac => igrac.id === action.igracUlaziId);

    if (indexIzlazi !== -1 && indexUlazi !== -1) {
      [igraci[indexIzlazi], igraci[indexUlazi]] = [igraci[indexUlazi], igraci[indexIzlazi]];
    }

    return { ...state, igraci };
  }),
);
