import { Component, OnInit } from '@angular/core';
import { Observable, of } from 'rxjs';
import { Store } from '@ngrx/store';
import { Router } from '@angular/router';
import { CookieService } from 'ngx-cookie-service';


import {
  loadTimById,
  loadIgraciTeren,
  loadGolmani,
  loadOdbrambeni,
  loadVeznjaci,
  loadNapadaci,
  loadTrenutniKorisnik,
} from './store/igrac.action';
import { KorisnikService } from './services/korisnik.service';
import { AuthService } from './services/auth.service';
import { JwtService } from './services/jwt.service';
import { Igrac } from './models/igrac';
import { AppState } from './app.state';
import { selectIgraciZamena } from './store/igrac.selector';
import { Emitters } from './emitters/emitters';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  trenutniTimID: number | undefined;
  trenutniKorisnik$: Observable<any> | undefined;
  trenutniKorisnikID: number | null;

  igraciZamena$: Observable<Igrac[]> = of([]);

  constructor(
    // private FudbalskitimService: FudbalskitimService,
    private store: Store<AppState>,
    private korisnikService: KorisnikService,
    private authService: AuthService,
    private jwtService: JwtService,
    private cookieService: CookieService,
    private router: Router
  ) {
    this.igraciZamena$ = this.store.select(selectIgraciZamena);
    this.trenutniKorisnikID = null; // Postavljamo na null dok ne dobijemo prave podatke
  }

  ngOnInit(): void {
    Emitters.authEmitter.emit(true);
    // Proveri da li je korisnik prijavljen
    if (this.authService.isLoggedIn()) {
      // Uzmite ID trenutno prijavljenog korisnika
      const token = this.jwtService.getToken();
      console.log(token)
      if (token) {
        const decodedToken = atob(token.split('.')[1]);
        console.log(decodedToken)
        const parsedToken = JSON.parse(decodedToken);
        const userId = parsedToken.id; // Promeniti na odgovarajuće ime polja u tokenima
        console.log(userId);

        // Postavite trenutni korisnik ID
        this.trenutniKorisnikID = userId;

        // Dobijemo trenutnog korisnika i njegov tim ID
        this.trenutniKorisnik$ = this.korisnikService.getKorisnikById(userId);

        this.trenutniKorisnik$.subscribe((korisnik) => {
          if (korisnik) {
            this.trenutniTimID = korisnik.timId;
            if(this.trenutniTimID){
            this.store.dispatch(loadTimById({ timID: this.trenutniTimID }));
            // this.store.dispatch(loadIgraciZamena({ timID: this.trenutniTimID }));
            this.store.dispatch(loadIgraciTeren({ timID: this.trenutniTimID }));
            this.store.dispatch(loadGolmani({ timID: this.trenutniTimID }));
            this.store.dispatch(loadOdbrambeni({ timID: this.trenutniTimID }));
            this.store.dispatch(loadVeznjaci({ timID: this.trenutniTimID }));
            this.store.dispatch(loadNapadaci({ timID: this.trenutniTimID }));
            }

          } else {
            console.error('Nije moguće pronaći trenutnog korisnika.');
          }
        });

        // Učitaj trenutnog korisnika
        this.store.dispatch(loadTrenutniKorisnik({ korisnikId: userId }));
      }
    } else {
      console.log('Korisnik nije prijavljen.');
    }
  }

  title = 'AngularProjekat';
}
