import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Emitters } from 'src/app/emitters/emitters';

@Component({
  selector: 'app-nav',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.css']
})
export class NavComponent implements OnInit {
  authenticated = false;

  constructor(private http:HttpClient) {}

  ngOnInit(): void {
    Emitters.authEmitter.subscribe(
      (auth: boolean) => {
        this.authenticated = auth;
      }
    );
  }

  logout() : void {
    this.http.post('http://localhost:3000/api/logout', {}, {withCredentials:true}).subscribe(()=>{
      this.authenticated = false;
    })
  }
}


<nav class="navbar navbar-expand-md navbar-dark bg-dark mb-4">
  <div class="container-fluid">
    <a class="navbar-brand" routerLink="/">Home</a>
    <div>
      <ul class="navbar-nav me-auto mb-2 mb-md-0" *ngIf="!authenticated">
        <li class="nab-item">
          <a class="nav-link" aria-current="page" routerLink="/login">Login</a>
        </li>
        <li class="nab-item">
          <a class="nav-link" aria-current="page" routerLink="/register">Register</a>
        </li>
      </ul>

      <ul class="navbar-nav me-auto mb-2 mb-md-0" *ngIf="authenticated">
        <li class="nab-item">
          <a class="nav-link" aria-current="page" routerLink="/login" (click)="logout()">Logout</a>
        </li>
      </ul>
    </div>
  </div>
</nav>
