  //   this.store.dispatch(loadTimById({ timID: this.trenutniTimID }));
  //   this.store.dispatch(loadIgraciTeren({ timID: this.trenutniTimID }));
  //   this.store.dispatch(loadGolmani({ timID: this.trenutniTimID }));
  //   this.store.dispatch(loadOdbrambeni({ timID: this.trenutniTimID }));
  //   this.store.dispatch(loadVeznjaci({ timID: this.trenutniTimID }));
  //   this.store.dispatch(loadNapadaci({ timID: this.trenutniTimID }));
